// import React from 'react';
import React, { useState, useEffect } from 'react';

const Posts = ({ posts, loading, photos ,q}) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  var count = 0;


  const ro={float:"right",
            display:"inline"}
  var count=0;



  return (
    <ul className='list-group mb-4'>
      {posts.map(post => (
      <>
        <li key={post.id} className='list-group-item'>
       <img  style={ro} height="200" width="200" src={photos[count%5].image} alt="Beer Name" />
        {count=count+1}
      <div>ID: {post.id}</div>
         <div>Name: {post.name}</div>
      <div>Style: {post.style}</div>
      <div>Ounces: {post.ounces}</div>
        </li>
        </>
      ))}
    </ul>
  );
};

export default Posts;
